using Models;
using Newtonsoft.Json;
using NUnit.Framework;
using System;
using System.Collections.Generic;
using System.IO;
using System.Net;
using System.Net.Http;
using System.Text;
using System.Threading.Tasks;
using System.Web.Script.Serialization;
using TechTalk.SpecFlow;
using Utf8Json;
using ViewModels;

namespace RestApiTests
{
    [Binding]
    public class AddStudentFeatureSteps
    {
        StudentViewModel studentViewModel;
        DataPlusToken dataPlusToken;
        string ResponseString = "";
        HttpWebResponse response = null;

        [Given(@"student data is given")]
        public void GivenStudentDataIsGiven()
        {
            StudentApiFeatureSteps steps = new StudentApiFeatureSteps();
            steps.GivenUsernameIs("Kamran");
            steps.GivenPasswordIs("12345");
            steps.WhenLoginRequestIsCalled();
            dataPlusToken = steps.ThenRecieveAccessToken();
            if(dataPlusToken != null) {
                studentViewModel = new StudentViewModel
                {
                    Student = new Student
                    {
                        Name = "Awais Khan",
                        Email = "awais786@yahoo.com",
                        Dob = new DateTime(2012, 4, 3),
                        Phone = "03160578748",
                        Password = "Awais1",
                        ConfirmPassword = "Awais1",
                        UserId = Convert.ToInt32(dataPlusToken.UserId)
                    },
                    Courses = new List<string>() { "1", "2", "3" },
                    ImageFile = new string[1] { "" }
                };
            }
        }
        
        [When(@"form is submitted")]
        public void WhenFormIsSubmitted()
        {
            try
            {
                JavaScriptSerializer jss = new JavaScriptSerializer();
                var myContent = jss.Serialize(studentViewModel);
                Client client = new Client();
                ResponseString = client.Request("api/student",myContent,"POST",false,false);
            }
            catch (WebException ex)
            {
                if (ex.Status == WebExceptionStatus.ProtocolError)
                {
                    response = (HttpWebResponse)ex.Response;
                    ResponseString = "Some error occured: " + response.StatusCode.ToString();
                }
                else
                {
                    ResponseString = "Some error occured: " + ex.Status.ToString();
                }
            }
        }
        
        [Then(@"post student")]
        public void ThenPostStudent()
        {
            Assert.AreEqual("\"Posted student\"", ResponseString);  
        }
    }
}
